mapper.select:
select *
from
(
    select rownum as RNUM, alist.*
    from(
        select * from addrBookView
        where addr_isdel='N'

			and addr_groupno=#{addrGroupNo}		


        order by addr_no		        
    ) alist
) 

where RNUM > #{firstRecordIndex} 
and RNUM<=#{firstRecordIndex}+ #{recordCountPerPage}

select *
from
(
    select rownum as RNUM, dlist.*
    from(
        select * from addrBookView
        where addr_isdel='Y'
        order by emp_No		        
    ) dlist
) 

where RNUM > #{firstRecordIndex} 
and RNUM<=#{firstRecordIndex}+ #{recordCountPerPage}

mapper.update:
update addrBook
set addr_isdel='Y'
where addr_no=#{addrNo}

update addrBook
set addr_isdel='N'
where addr_no=#{addrNo}
