mapper.update:
update commute
set cmt_out=sysdate,cmt_status='N'
where cmt_no=#{cmtNo}

mapper.select:
select count(*) from cmtView
where to_char(cmt_in,'yyyy-mm-dd')=to_char(sysdate,'yyyy-mm-dd')
and emp_no=#{empNo}

select count(*) from cmtView
where to_char(cmt_out,'yyyy-mm-dd')=to_char(sysdate,'yyyy-mm-dd')
and emp_no=#{empNo}
