mapper.update:
update doc_form 
set form_name = #{formName}, form_secu=#{formSecu}, form_life=#{formLife}, form_ex=#{formEx} 
where form_no = #{formNo}

mapper.select:
select * from (
    select rownum as RNUM, blist.* from
    (
        select * from confirm 
        where cf_status = #{listType} and emp_no=#{empNo}
        and (cf_no like '%' || #{searchKeyword} || '%' or cf_title like '%' || #{searchKeyword} || '%' or cf_content like '%' || #{searchKeyword} || '%')
        order by cf_regdate desc
    ) blist
)

where RNUM>#{firstRecordIndex} and RNUM<=#{firstRecordIndex}+#{recordCountPerPage}

select * from (
    select rownum as RNUM, blist.* from
    (
        select * from confirm 
        where cf_status = #{listType} and (cf_confirmer = #{cfConfirmer} or emp_no=#{empNo})
        and (cf_no like '%' || #{searchKeyword} || '%' or cf_title like '%' || #{searchKeyword} || '%' or cf_content like '%' || #{searchKeyword} || '%')
        order by cf_regdate desc
    ) blist
)

where RNUM>#{firstRecordIndex} and RNUM<=#{firstRecordIndex}+#{recordCountPerPage}

mapper.update:
update confirm set link_cf_no = #{linkCfNo}, cf_title = #{cfTitle}, cf_content = #{cfContent}, 
cf_isfile = #{cfIsfile}, cf_Status = #{cfStatus}, cf_confirmer = #{cfConfirmer}
where cf_no = #{cfNo}
