mapper.select:
SELECT *
FROM
    (
        SELECT
            rownum AS RNUM,
            mlist.*
        FROM (
                 SELECT m.*
                 FROM MSGRECVIEW m
                 WHERE EMP_NO = #{empNo}

                 ORDER BY MSG_REGDATE DESC
             ) mlist
    )

WHERE RNUM > #{firstRecordIndex}
      AND RNUM <= #{firstRecordIndex} + #{recordCountPerPage}

SELECT * FROM( SELECT ROWNUM AS RNUM, mlist.* FROM( SELECT MSG_NO, MIN(EMP_NAME) as EMP_NAME, MIN(EMP_NO) as EMP_NO, MIN(MSG_TITLE) as MSG_TITLE, MIN(MSG_REGDATE) as MSG_REGDATE, count(MSG_NO) as CNT FROM MSGSENDVIEW
GROUP BY MSG_NO ORDER BY MSG_NO DESC ) mlist )
WHERE RNUM > #{firstRecordIndex} AND RNUM <= #{firstRecordIndex} + #{recordCountPerPage}

SELECT * FROM ( SELECT rownum AS RNUM, mlist.* FROM ( SELECT m.* FROM MSGRECVIEW m WHERE EMP_NO = #{empNo} and MSG_IMPFLAG = 'Y'
ORDER BY MSG_REGDATE DESC ) mlist )
WHERE RNUM > #{firstRecordIndex} AND RNUM <= #{firstRecordIndex} + #{recordCountPerPage}

mapper.update:
UPDATE MESSAGE_REC
SET MSG_READFLAG = 'Y'
WHERE REC_NO = #{recNo}
